                $data['_view'] = 'product_cat/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The product_cat you are trying to edit does not exist.');
    } 

    /*
     * Deleting product_cat
     */
    function remove($pcid)
    {
        $product_cat = $this->Product_cat_model->get_product_cat($pcid);

        // check if the product_cat exists before trying to delete it
        if(isset($product_cat['pcid']))
        {
            $this->Product_cat_model->delete_product_cat($pcid);
            redirect('product_cat/index');
        }
        else
            show_error('The product_cat you are trying to delete does not exist.');
    }
    
